sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.info("AYAN0MURSHAD : ");
    try {
        var r = new sn_ws.RESTMessageV2('x_980818_ayanmursh.Zoom Integration', 'Create_meeting');
        r.setStringParameterNoEscape('topic', current.subject);
        r.setStringParameterNoEscape('duration', current.duration);
        r.setStringParameterNoEscape('start_time', current.start_time);
        r.setStringParameterNoEscape('mute_upon_entry', current.mute_upon_entry);
        r.setStringParameterNoEscape('participant_video', current.participant_video);
        r.setStringParameterNoEscape('host_video', current.host_video);
        r.setStringParameterNoEscape('watermark', current.watermark);
        r.setStringParameterNoEscape('type', '2');
        r.setStringParameterNoEscape('join_before_host', 'true');
        r.setStringParameterNoEscape('userid', 'me');
        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        gs.info("AYAN0MURSHAD : " + responseBody);
        gs.info("AYAN0MURSHAD : " + responseBody.start_url);
        current.u_url_1 = responseBody.start_url;
        current.u_string_3 = responseBody;

    } catch (ex) {
        var message = ex.message;
        gs.log("AYAN0MURSHAD : " + message);
    }

})(current, previous);




