sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    this.status = '200';
    var respObj = {}; //declare the response object
    var payload = request.body.data;
    var userPrincipalName = payload.userPrincipalName;
    var scop = payload.scop;
    var domainname = payload.domainname;
    var groupname = payload.groupname;
    var user = userglide();
    var group = groupglide();
    var grp = new GlideRecord('sys_user_grmember');
    grp.addQuery('user', user);
    grp.addQuery('group', group);
    grp.query();
    if (grp.next()) {
        grp.deleteRecord();
        this.status = '200';
        respObj.body = {
            "OU Name": "OU=Accounts,DC=division-dc1,DC=local",
            "statusMessage": "Successfully Delted From the group.",
            "Name": groupname,
            "User Email ": userPrincipalName,
            "status": "200"
        };

    } else {
        this.status = '402';
        respObj.body = {
            "ou_name": "OU=Accounts,DC=division-dc1,DC=local",
            "statusMessage": "User not in Group",
            "Name": groupname,
            "User Email ": userPrincipalName,
            "status": "402"
        };
    }


    if (this.status == '200') {
        response.setBody(respObj);
        response.setStatus(this.status);
        return response;
    } else {
        var setError = new sn_ws_err.ServiceError(); //this API used to set the custom error messages
        setError.setStatus(this.status);
        setError.setMessage(respObj.body.statusMessage);
        setError.setDetail(respObj.body.ou_name);
        return setError;
    }

    function userglide() {
        var groupu = new GlideRecord('sys_user');
        groupu.addQuery('email', userPrincipalName);
        groupu.query();
        if (groupu.next()) {
            return groupu.getUniqueValue();
        }
    }

    function groupglide() {
        var groupu = new GlideRecord('sys_user_group');
        groupu.addQuery('name', groupname);
        groupu.query();
        if (groupu.next()) {
            return groupu.getUniqueValue();
        }
    }
})(request, response);
